import React, { useState } from 'react';
import { MdAdd } from 'react-icons/md';
import styled, { css } from 'styled-components';

const TodoCreate = () => {
	const [open, setOpen] = useState(false);

	return (
		<>
			<CircleBtn>
				<MdAdd />
			</CircleBtn>
		</>
	);
};

const CircleBtn = styled.button`
	background: #38d9a9;
	&:hover {
		background: #63e6be;
	}
	&:active {
		background: #20c997;
	}

	z-index: 5;
	cursor: pointer;
	width: 80px;
	height: 80px;
	display: block;

	font-size: 60px;
	position: absolute;
	left: 50%;
	bottom: 0px;
	transform: translate(-50%, 50%);
	color: #fff;
	border: 50%;
	border: none;
	display: flex;
	align-items: center;
	justify-content: center;
`;

export default TodoCreate;
